def agrupar_emprego(emprego):
  if 'Student' in emprego:
    return 'Estudante'
  elif 'Employed full-time' in emprego:
    return 'Trabalhando'
  elif 'Retired' in emprego:
    return 'Aposentado'
  elif 'Independent contractor, freelancer, or self-employed' in emprego:
    return 'Aut√¥nomo'
  else:
    return 'Outro'


